arguments.argument:
var xr, yr;
xr = round(x);
yr = round(y);

// draw the cross
if (crossSize > 0) {
    draw_line_color(xr + crossSize, yr, xr - crossSize, yr, crossColor, crossColor);
    draw_line_color(xr, yr + crossSize, xr, yr - crossSize, crossColor, crossColor);
}

// draw a vector if defined
if (vector) {
    draw_line_color(xr, yr, round(vectorX), round(vectorY), vectorColor, vectorColor);
}
