arguments.argument:
{
    // set the needler's sprites based on its ammo count
    if (ammoCount > 0) {
        // has ammo
        normalSprite = NeedlerBaseS;
        grenadeSprite = NeedlerBaseGrenadeS;
        meleeSprite = NeedlerMeleeS;
        dualSprite = NeedlerBaseDWS;
    } else {
        // no ammo
        normalSprite = NeedlerEmptyBaseS;
        grenadeSprite = NeedlerEmptyBaseGrenadeS;
        meleeSprite = NeedlerEmptyMeleeS;
        dualSprite = NeedlerEmptyBaseDWS;
    }
}
